VALUES( nolgos_seq.nextval, '올림픽공원 스케이트장',1,(SELECT no FROM locations WHERE  depth1 LIKE '%서울특별시%' AND depth2 LIKE '%송파구%' AND depth3 is null  ),7,systimestamp,0,0,0,'127.1328749170','37.5212095112','서울특별시 송파구 올림픽로 424 (방이동)','<p><a href="http://www.olympicpark.co.kr/jsp/homepage/contents/sports/skate_main.jsp" target="_blank" title="새창 : 올림픽스케이트장 홈페이지로 이동">http://www.olympicpark.co.kr</a></p>' || '<p>' || '서울 송파구에 위치한 올림픽공원 평화의 광장 스케이트장이다. 올림픽 기념관, 소마미술관, 몽촌 토성 등 주변 볼거리가 다양할 뿐만 아니라 다양한 이벤트 행사를 개최하여 즐길 거리도 많이 준비되어 있다. <br /><br />부대시설로는 야외 스케이트장 주변에 마련된 휴게실, 스케이트 대여실, 매점, 스낵바 등이 있다. <br>' || '</p>','def(1024x440).png','010-1234-1234','0200',1000,10000,'연중무휴');
