VALUES( nolgos_seq.nextval, '강동선사문화축제 2018',1,(SELECT no FROM locations WHERE  depth1 LIKE '%서울특별시%' AND depth2 LIKE '%강동구%' AND depth3 is null  ),2,systimestamp,0,0,0,'127.1303240272','37.5606243328','서울시 관악구 옐로우 빌딩 14층 글로벌 아이티','<p><a href="http://culture.gangdong.go.kr" target="_blank" title="새창:강동선사문화축제 안내 페이지로 이동">http://culture.gangdong.go.kr</a></p><p><img src="http://tong.visitkorea.or.kr/cms/resource/61/2541361_image2_1.JPG"/></p>' || '<p>' || '&lt;강동선사문화축제&gt;는 6,000년 전 신석기인들의 숨결이 서려있는 암사동유적 (국가사적 267호)에서 매년 10월에 3일간의 일정으로 개최된다. 1996년 시작해 해마다 테마를 정해 축제에 새로운 색감을 더하여 해마다 발전을 거듭하고 있으며, 올해로 제23회를 맞은 &lt;선사문화축제&gt;는 10월 12일부터 14일까지 진행된다.<br>' || '</p>','http://tong.visitkorea.or.kr/cms/resource/61/2541361_image2_1.JPG','010-1234-1234','0200',1000,10000,'연중무휴');
