VALUES( nolgos_seq.nextval, '비스타 워커힐 서울',1,(SELECT no FROM locations WHERE  depth1 LIKE '%서울특별시%' AND depth2 LIKE '%광진구%' AND depth3 is null  ),8,systimestamp,0,0,0,'127.1116924462','37.5586325612','서울특별시 광진구 워커힐로 177 (광장동)','<p><a href="http://www.vistawalkerhillseoul.com" target="_blank" title="새창 : 비스타워커힐서울 홈페이지로 이동">http://www.vistawalkerhillseoul.com</a></p>' || '<p>' || '사계절이 아름다운 아차산과 한강의 에너지를 그대로 품은 비스타 워커힐 서울에서 가슴 설레는 여행을 시작해 보길 바란다. 비스타 호텔은 자연 친화적인 환경, 세련되고 품격있는 인테리어, 최신의 테크놀로지를 접목한 서비스와 함께 새로운 라이프 스타일 트렌드를 선도한다. 서울시내 최고의 뷰를 자랑하는 도심 속 휴양지 비스타 워커힐 서울과 함께 그 어디에서도 경험할 수 없는 차별화된 가치를 만나볼 수 있다. <br>' || '</p>','def(1024x440).png','010-1234-1234','0200',1000,10000,'연중무휴');
