VALUES( nolgos_seq.nextval, '남산 야외식물원',1,(SELECT no FROM locations WHERE  depth1 LIKE '%서울특별시%' AND depth2 LIKE '%용산구%' AND depth3 LIKE '%한남동%'  ),8,systimestamp,0,0,0,'126.9957312493','37.5422490442','서울특별시 용산구 한남동 772-12 일원','<p>서울의 산과 공원 <a href="http://parks.seoul.go.kr" target="_blank" title="새창: 서울의 산과 공원 홈페이지로 이동">http://parks.seoul.go.kr</a></p><p><img src="http://tong.visitkorea.or.kr/cms/resource/06/1568006_image2_1.jpg"/></p>' || '<p>' || '1994년 철거한 용산구 한남동 외인주택터에 만들어진 식물원을 1997년 2월 18일에 개원되었다. 면적은 59.241㎡이며 보유식불은 13개의 주제로 나뉘어져 있고 서울지방에서 자랄 수 있는 식물 총 269종 117,132주가 심어져 있다. 이중 나무 종류는 소나무 등 129종 60,912주이고 풀 종류는 할미꽃 등 140종 56,220본이다.<br>' || '</p>','http://tong.visitkorea.or.kr/cms/resource/06/1568006_image2_1.jpg','010-1234-1234','0200',1000,10000,'연중무휴');
